import { useState } from "react"
import { AiOutlineEye, AiOutlineEyeInvisible} from "react-icons/ai"


function SignupForm(){

  const [formData,setFormData] = useState({
    firstName:"",
    lastName:"",
    email:"",
    password:"",
    confirmPassword:""
  })
  
  const[showPassword,setShowPassword] = useState(false);
  const[showPasswordconfirm,setShowPasswordconfirm] = useState(false);

  function changeHandler(event){
    setFormData((prevData) =>({
        ...prevData,
        [event.target.name]:event.target.value
    }))
  }

  function submitHandler(event){
     event.preventDefault();
     toast.success("Account created succcessfull")
  }
 return(
    <div>

       <div>
        <button>Student</button>
        <button>Instructor</button>
       </div>

       <form onClick={submitHandler}>

         <div>
         <label>
            <p>First Name<sup>*</sup></p>
            <input required type="text"  name="firstName" onChange={changeHandler}
             placeholder="Enter First Name"  value={formData.firstName}></input>
          </label>

          <label>
            <p>last Name<sup>*</sup></p>
            <input required type="text"  name="lastName" onChange={changeHandler}
             placeholder="Enter last Name"  value={formData.lastName}></input>
          </label>
         </div>
 
         <label>
            <p>Email Address<sup>*</sup></p>
            <input required type="email"  name="email" onChange={changeHandler}
             placeholder="Enter email"  value={formData.email}></input>
          </label>

          <div>
         <label>
            <p>password<sup>*</sup></p>
            <input required type={showPassword?("text"):("password")}  name="password" onChange={changeHandler}
             placeholder="Enter password"  value={formData.password}></input>

             <span onClick={()=>setShowPassword((prev)=>!prev)}>
             {showPassword ? (<AiOutlineEye/>):(<AiOutlineEyeInvisible/>)}
             </span>
          </label>

          <label>
            <p>Confirm Password<sup>*</sup></p>
            <input required type={showPasswordconfirm?("text"):("password")}  name="confirmPassword" onChange={changeHandler}
             placeholder="Please Confirm Password"  value={formData.confirmPassword}></input>

             <span onClick={()=>setShowPasswordconfirm((prev)=>!prev)}>
             {showPasswordconfirm ? (<AiOutlineEye/>):(<AiOutlineEyeInvisible/>)}
             </span>
          </label>
         </div>
        <button>Create Account</button>
       </form>
    </div>
 )
}

export default SignupForm
